    setData(undefined);
    setError(undefined);

    try {
      const result = await queryFn();
      setData(result);
      setError(undefined);
      setIsPending(false);
    } catch (error) {
      setData(undefined);
      setError(error as TError);
      setIsPending(false);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [queryKey]);

  return {
