// useQuery.ts

// eslint-disable-next-line @typescript-eslint/no-explicit-any
type UseQueryProps<T = any> = {
  queryKey: string;
  queryFn: () => Promise<T>;
  cache?: boolean;
  cacheTime?: number;
  retry?: boolean;
};

const MAXIMUM_RETRY = 5; // ìµœëŒ€ retry íšŸìˆ˜
const DEFAULT_CACHE_TIME = 60 * 1000; // ìºì‹œ ë””í´íŠ¸ ì €ì¥ ì‹œê°„, 1ë¶„

// eslint-disable-next-line @typescript-eslint/no-explicit-any
export function useQuery<TData = any, TError = any>({
  queryKey,
  queryFn,
  cache,
  cacheTime = DEFAULT_CACHE_TIME,
  retry,
}: UseQueryProps) {
  const [isPending, setIsPending] = useState(true);
  const [data, setData] = useState<TData | undefined>();
  const [error, setError] = useState<TError | undefined>();
  const queryCache = useMemo(() => QueryCache.getInstance(), []);

  useEffect(() => {
    let active = true;
    setIsPending(true);
    setData(undefined);
    setError(undefined);

    const handleResult = (result: TData) => {
      // í™œì„± ìƒíƒœê°€ ì•„ë‹˜
      if (!active) return;

      // ìºì‹œì— ì €ì¥
      if (cache) {
        queryCache.writeCache({ key: queryKey, value: result, cacheTime });
      }

      setData(result);
      setError(undefined);
      setIsPending(false);
    };

    const handleError = (error: TError) => {
      // í™œì„± ìƒíƒœê°€ ì•„ë‹˜
      if (!active) return;

      setData(undefined);
      setError(error as TError);
      setIsPending(false);
    };

    const fetchWithRetry = async (retry = 0) => {
      try {
        const result = await queryFn();
        handleResult(result);
      } catch (error) {
        if (retry + 1 < MAXIMUM_RETRY) {
          fetchWithRetry(retry + 1);
        } else {
          handleError(error as TError);
        }
      }
    };

    const fetchWithoutRetry = async () => {
      try {
        const result = await queryFn();
        handleResult(result);
      } catch (error) {
        handleError(error as TError);
      }
    };

    const cachedData = queryCache.readCache(queryKey);
    if (cache && cachedData) {
      setData(cachedData.value as TData);
      setIsPending(false);
      setError(undefined);
    } else {
      if (retry) {
        fetchWithRetry();
      } else {
        fetchWithoutRetry();
      }
    }

    return () => {
      active = false;
    };

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [queryKey, cache, cacheTime, retry]);

  return {
    isPending,
    error,
    data,
  };
}
